catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var gaUser = new GlideAjax('userInfoUtils'); // userInfoUtils is the script include class 
    gaUser.addParam('sysparm_name', 'fetchUserName'); // getUserName is the script include method 
    gaUser.addParam('sysparm_user_sys_id', g_form.getValue('requested_for')); // Set parameter sysparm_user_name to new Value on field 
    gaUser.getXML(identifyUser);
    /* Call HelloWorld.helloWorld() with the parameter sysparm_user_name set to 'Bob' 
        and use the callback function userInfoParse() to return the result when ready */
}

// the callback function for returning the result from the server-side code
function identifyUser(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    alert(answer);
	g_form.setValue('user_name', answer);
}
