catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
	//find Location
    var gaLocation = new GlideAjax('userInfoUtils'); // userInfoUtils is the script include class 
    gaLocation.addParam('sysparm_name', 'getLocation'); // getLocation is the script include method 
    gaLocation.addParam('sysparm_user_sys_id', g_form.getValue('employee_leaving')); // Set parameter sysparm_user_sys_id that inhabits 'employee_leaving' field 
    gaLocation.getXML(findLocation);
    /* Call userInfoUtils.getLocation() with the value in sysparm_user_sys_id  
        and use the callback function findLocation() to return the result when ready */

	//find Phone Number
    var gaPhone = new GlideAjax('userInfoUtils'); // userInfoUtils is the script include class 
    gaPhone.addParam('sysparm_name', 'getUserPhone'); // getUserPhone is the script include method 
    gaPhone.addParam('sysparm_user_sys_id', g_form.getValue('employee_leaving')); // Set parameter sysparm_user_sys_id that inhabits 'employee_leaving' field 
    gaPhone.getXML(findPhone);
    /* Call userInfoUtils.getUserPhone() with the value in sysparm_user_sys_id  
        and use the callback function findPhone() to return the result when ready */

	//find Email
    var gaEmail = new GlideAjax('userInfoUtils'); // userInfoUtils is the script include class 
    gaEmail.addParam('sysparm_name', 'getUserEmail'); // getUserEmail is the script include method 
    gaEmail.addParam('sysparm_user_sys_id', g_form.getValue('employee_leaving')); // Set parameter sysparm_user_sys_id that inhabits 'employee_leaving' field 
    gaEmail.getXML(findEmail);
    /* Call userInfoUtils.getUserEmail() with the value in sysparm_user_sys_id  
        and use the callback function findEmail() to return the result when ready */

}

// the callback function for returning the result from the server-side code
//find Location callback
function findLocation(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('location', answer);
}

//find Phone callback
function findPhone(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('contact_number', answer);
}

//find email callback
function findEmail(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('email', answer);

}
