sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

var gaUser = new GlideAjax('HelloWorld'); // HelloWorld is the script include class 
gaUser.addParam('sysparm_name','helloWorld'); // helloWorld is the script include method 
gaUser.addParam('sysparm_user_name',"Bob"); // Set parameter sysparm_user_name to 'Bob' 
gaUser.getXML(HelloWorldParse);  /* Call HelloWorld.helloWorld() with the parameter sysparm_user_name set to 'Bob' 
      and use the callback function HelloWorldParse() to return the result when ready */
}

// the callback function for returning the result from the server-side code
function HelloWorldParse(response) {  
   var answer = response.responseXML.documentElement.getAttribute("answer"); 
    alert(answer);
}
