sys_script_include.script:
var userInfoUtils = Class.create();
userInfoUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	fetchUserName: function(){
		var userID = this.getParameter('sysparm_user_sys_id');
		var result = '';

		var grUserInfo = new GlideRecord('sys_user');
		grUserInfo.addQuery('sys_id', userID);
		grUserInfo.query(); 

		if (grUserInfo.next()){

			result = grUserInfo.getValue('user_name'); 

		} 
		return result; 
	}, 

	getLocation: function(){
		var location = ''; 
		var userProfile = this.getParameter('sysparm_user_sys_id');

		var grLocation = new GlideRecord('sys_user');
		grLocation.addQuery('sys_id', userProfile);
		grLocation.query(); 

		if (grLocation.next()){

			location = grLocation.getValue('location'); 

		} 
		return location;

	},

	getUserPhone: function(){
		var phoneNumber = ''; 
		var userProfile = this.getParameter('sysparm_user_sys_id');

		var grPhoneNumber = new GlideRecord('sys_user');
		grPhoneNumber.addQuery('sys_id', userProfile);
		grPhoneNumber.query(); 

		if (grPhoneNumber.next()){

			phoneNumber = grPhoneNumber.getValue('phone'); 

		} 
		return phoneNumber;


	},

	getUserEmail: function(){
		var userEmail = ''; 
		var userProfile = this.getParameter('sysparm_user_sys_id');

		var grEmail = new GlideRecord('sys_user');
		grEmail.addQuery('sys_id', userProfile);
		grEmail.query(); 

		if (grEmail.next()){

			userEmail = grEmail.getValue('email'); 

		} 
		return userEmail;

	},

    type: 'userInfoUtils'
});
